create table partner
(
    id              bigserial   NOT NULL,
    phone1          varchar(50) NOT NULL,
    phone2          varchar(50) NOT NULL,
    address         varchar(50) NOT NULL,
    longitude       numeric     NOT NULL,
    latitude        numeric     NOT NULL,
    is_promary      boolean     NOT NULL default true,
    created_date    timestamp   NOT NULL default NOW(),

    CONSTRAINT pk__partner_id PRIMARY KEY (id)
);

create table partners_contact_information
(
    id   bigserial   NOT NULL,
    name varchar(50) NOT NULL,

    CONSTRAINT pk__partners_contact_information_id PRIMARY KEY (id)
);

create table partner_skill
(
    id         bigserial   NOT NULL,
    name       varchar(50) NOT NULL,
    partner_id bigint      NOT NULL,

    CONSTRAINT pk__partner_skill_id PRIMARY KEY (id),
    CONSTRAINT unq__partner_skill UNIQUE (name, partner_id),
    CONSTRAINT fk__partner_skill_to_partner FOREIGN KEY (partner_id)
        REFERENCES partner (id) ON delete CASCADE ON update CASCADE
);

create table job_detail
(
    id           bigserial   NOT NULL,
    job_id       varchar(50) NOT NULL,
    client_name  varchar(50) NOT NULL,
    client_phone varchar(50) NOT NULL,
    timestamp    timestamp   NOT NULL,
    job_type     varchar(50) NOT NULL,
    address      varchar(50) NOT NULL,
    city         varchar(50) NOT NULL,
    longitude    numeric     NOT NULL,
    latitude     numeric     NOT NULL,

    CONSTRAINT pk__job_detail_id PRIMARY KEY (id)
);
